scripts/import-manufacturer-docs.js.content:
import { readFile } from 'fs/promises';
import axios from 'axios';

const API_BASE_URL = process.env.API_URL || 'http://localhost:5000';

function extractManufacturer(title, url) {
  // Check title prefix first
  if (title.startsWith('GAF ')) return 'GAF';
  if (title.startsWith('Carlisle ')) return 'Carlisle';
  if (title.startsWith('Versico ')) return 'Versico';
  if (title.startsWith('Johns Manville ') || title.startsWith('JM ')) return 'Johns Manville';
  if (title.startsWith('Holcim ')) return 'Holcim Elevate';

  // Fall back to URL detection
  if (url.includes('gaf.com')) return 'GAF';
  if (url.includes('versico.com')) return 'Versico';
  if (url.includes('carlisle')) return 'Carlisle';
  if (url.includes('jm.com')) return 'Johns Manville';
  if (url.includes('holcim')) return 'Holcim Elevate';

  return 'GAF'; // Default
}

function extractSystemType(title, url) {
  const text = `${title} ${url}`.toLowerCase();

  if (text.includes('tpo')) return 'TPO';
  if (text.includes('pvc')) return 'PVC';
  if (text.includes('epdm')) return 'EPDM';
  if (text.includes('asphalt') || text.includes('asphaltic') || text.includes('mod bit')) return 'Asphalt';
  if (text.includes('coating') || text.includes('liquid')) return 'Coatings';
  if (text.includes('insulation') || text.includes('polyiso') || text.includes('iso')) return 'Insulation';
  if (text.includes('fastener') || text.includes('adhesive') || text.includes('accessor') || text.includes('edge') || text.includes('metal')) return 'Accessories';

  return 'General';
}

function mapCategoryToDocType(category, title) {
  const catLower = category.toLowerCase();
  const titleLower = title.toLowerCase();

  if (catLower.includes('warrant') || catLower.includes('guarantee')) return 'Warranty';
  if (catLower.includes('safety') || catLower.includes('sds')) return 'Safety Data Sheet';
  if (catLower.includes('technical bulletin')) return 'Technical Bulletin';
  if (catLower.includes('specification')) return 'Specification';
  if (catLower.includes('installation') || catLower.includes('detail')) return 'Installation Guide';
  if (titleLower.includes('data sheet') || titleLower.includes('pds')) return 'Product Data Sheet';
  if (titleLower.includes('installation') || titleLower.includes('detail')) return 'Installation Guide';
  if (titleLower.includes('specification') || titleLower.includes('spec')) return 'Specification';

  return 'Technical Bulletin';
}

async function saveDocument(doc) {
  try {
    const response = await axios.post(`${API_BASE_URL}/api/manufacturer-documents`, doc);
    return { success: true, data: response.data };
  } catch (error) {
    if (error.response?.status === 500 && error.response?.data?.message?.includes('duplicate')) {
      return { success: false, duplicate: true };
    }
    return { success: false, error: error.response?.data?.message || error.message };
  }
}

async function importDocuments() {
  console.log('üì¶ Importing comprehensive manufacturer documents...\n');

  const jsonData = await readFile('attached_assets/complete_commercial_roofing_documents_with_carlisle_1759271288402.json', 'utf-8');
  const documents = JSON.parse(jsonData);

  console.log(`Found ${documents.length} documents in JSON file\n`);

  let successCount = 0;
  let duplicateCount = 0;
  let errorCount = 0;
  const seenUrls = new Set();

  for (let i = 0; i < documents.length; i++) {
    const doc = documents[i];

    if (seenUrls.has(doc.url)) {
      duplicateCount++;
      continue;
    }
    seenUrls.add(doc.url);

    const manufacturer = extractManufacturer(doc.title, doc.url);
    const documentType = mapCategoryToDocType(doc.category, doc.title);
    const systemType = extractSystemType(doc.title, doc.url);

    const result = await saveDocument({
      manufacturer,
      documentTitle: doc.title,
      documentType,
      systemType,
      documentUrl: doc.url,
      thumbnailUrl: null
    });

    if (result.success) {
      successCount++;
      if (successCount % 1000 === 0) {
        console.log(`‚úì Imported ${successCount} documents...`);
      }
    } else if (result.duplicate) {
      duplicateCount++;
    } else {
      errorCount++;
      if (errorCount <= 5) {
        console.error(`‚úó Error: ${doc.title} - ${result.error}`);
      }
    }
  }

  console.log(`\nüìä Import Summary:`);
  console.log(`   ‚úì Successfully imported: ${successCount} documents`);
  console.log(`   ‚äò Skipped (duplicates): ${duplicateCount} documents`);
  console.log(`   ‚úó Errors: ${errorCount} documents`);
  console.log(`\n‚úÖ Import completed!`);
}

importDocuments().catch(console.error);

