import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();

declare module 'http' {
  interface IncomingMessage {
    rawBody: unknown
  }
}
app.use(express.json({
  verify: (req, _res, buf) => {
    req.rawBody = buf;
  }
}));
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertLeadSchema, insertContactSchema, insertManufacturerDocumentSchema } from "@shared/schema";
import { fromZodError } from "zod-validation-error";

export async function registerRoutes(app: Express): Promise<Server> {
  // Lead submission endpoint
  app.post("/api/leads", async (req, res, next) => {
    try {
      const result = insertLeadSchema.safeParse(req.body);

      if (!result.success) {
        const validationError = fromZodError(result.error);
        return res.status(400).json({ 
          message: validationError.message 
        });
      }

      const lead = await storage.createLead(result.data);
      res.status(201).json(lead);
    } catch (error: any) {
      next(error);
    }
  });

  // Contact submission endpoint
  app.post("/api/contacts", async (req, res, next) => {
    try {
      const result = insertContactSchema.safeParse(req.body);

      if (!result.success) {
        const validationError = fromZodError(result.error);
        return res.status(400).json({ 
          message: validationError.message 
        });
      }

      const contact = await storage.createContact(result.data);
      res.status(201).json(contact);
    } catch (error: any) {
      next(error);
    }
  });

  // Training courses endpoint
  app.get("/api/training/courses", async (req, res, next) => {
    try {
      const courses = await storage.getTrainingCourses();
      res.json(courses);
    } catch (error: any) {
      next(error);
    }
  });

  // Training resources endpoint
  app.get("/api/training/resources", async (req, res, next) => {
    try {
      const resources = await storage.getTrainingResources();
      res.json(resources);
    } catch (error: any) {
      next(error);
    }
  });

  // Manufacturer documents endpoint with pagination and filtering
  app.get("/api/manufacturer-documents", async (req, res, next) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : undefined;
      const offset = req.query.offset ? parseInt(req.query.offset as string) : undefined;
      const search = req.query.search as string | undefined;
      const manufacturers = req.query.manufacturers ? (req.query.manufacturers as string).split(',') : undefined;
      const systemTypes = req.query.systemTypes ? (req.query.systemTypes as string).split(',') : undefined;
      const documentTypes = req.query.documentTypes ? (req.query.documentTypes as string).split(',') : undefined;

      const filters = {
        search,
        manufacturers,
        systemTypes,
        documentTypes
      };

      const actualLimit = limit || 50;
      const actualOffset = offset || 0;

      const [documents, totalCount] = await Promise.all([
        storage.getManufacturerDocuments(actualLimit, actualOffset, filters),
        storage.getManufacturerDocumentsCount(filters)
      ]);

      res.json({
        documents,
        total: totalCount,
        limit: actualLimit,
        offset: actualOffset
      });
    } catch (error: any) {
      next(error);
    }
  });

  // Create manufacturer document endpoint
  app.post("/api/manufacturer-documents", async (req, res, next) => {
    try {
      const result = insertManufacturerDocumentSchema.safeParse(req.body);

      if (!result.success) {
        const validationError = fromZodError(result.error);
        return res.status(400).json({ 
          message: validationError.message 
        });
      }

      const document = await storage.createManufacturerDocument(result.data);
      res.status(201).json(document);
    } catch (error: any) {
      next(error);
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

