server/storage.ts.content:
import { 
  type Lead, 
  type InsertLead, 
  type Contact, 
  type InsertContact,
  type TrainingCourse,
  type InsertTrainingCourse,
  type TrainingResource,
  type InsertTrainingResource,
  type ManufacturerDocument,
  type InsertManufacturerDocument,
  leads,
  contacts,
  trainingCourses,
  trainingResources,
  manufacturerDocuments
} from "@shared/schema";
import { randomUUID } from "crypto";
import { db } from "./db";
import { asc, and, or, ilike, inArray, sql } from "drizzle-orm";

export interface ManufacturerDocumentFilters {
  search?: string;
  manufacturers?: string[];
  systemTypes?: string[];
  documentTypes?: string[];
}

export interface IStorage {
  createLead(lead: InsertLead): Promise<Lead>;
  getLeads(): Promise<Lead[]>;
  createContact(contact: InsertContact): Promise<Contact>;
  getContacts(): Promise<Contact[]>;
  getTrainingCourses(): Promise<TrainingCourse[]>;
  createTrainingCourse(course: InsertTrainingCourse): Promise<TrainingCourse>;
  getTrainingResources(): Promise<TrainingResource[]>;
  createTrainingResource(resource: InsertTrainingResource): Promise<TrainingResource>;
  getManufacturerDocuments(limit?: number, offset?: number, filters?: ManufacturerDocumentFilters): Promise<ManufacturerDocument[]>;
  getManufacturerDocumentsCount(filters?: ManufacturerDocumentFilters): Promise<number>;
  createManufacturerDocument(document: InsertManufacturerDocument): Promise<ManufacturerDocument>;
}

export class DbStorage implements IStorage {
  async createLead(insertLead: InsertLead): Promise<Lead> {
    const [lead] = await db.insert(leads).values(insertLead).returning();
    return lead;
  }

  async getLeads(): Promise<Lead[]> {
    return await db.select().from(leads);
  }

  async createContact(insertContact: InsertContact): Promise<Contact> {
    const [contact] = await db.insert(contacts).values(insertContact).returning();
    return contact;
  }

  async getContacts(): Promise<Contact[]> {
    return await db.select().from(contacts);
  }

  async getTrainingCourses(): Promise<TrainingCourse[]> {
    return await db.select().from(trainingCourses).orderBy(asc(trainingCourses.sortOrder));
  }

  async createTrainingCourse(course: InsertTrainingCourse): Promise<TrainingCourse> {
    const [newCourse] = await db.insert(trainingCourses).values(course).returning();
    return newCourse;
  }

  async getTrainingResources(): Promise<TrainingResource[]> {
    return await db.select().from(trainingResources).orderBy(asc(trainingResources.sortOrder));
  }

  async createTrainingResource(resource: InsertTrainingResource): Promise<TrainingResource> {
    const [newResource] = await db.insert(trainingResources).values(resource).returning();
    return newResource;
  }

  async getManufacturerDocuments(limit?: number, offset?: number, filters?: ManufacturerDocumentFilters): Promise<ManufacturerDocument[]> {
    const conditions = this.buildFilterConditions(filters);

    let query = db.select().from(manufacturerDocuments);

    if (conditions) {
      query = query.where(conditions) as any;
    }

    query = query.orderBy(asc(manufacturerDocuments.manufacturer), asc(manufacturerDocuments.documentTitle)) as any;

    if (limit !== undefined) {
      query = query.limit(limit) as any;
    }

    if (offset !== undefined) {
      query = query.offset(offset) as any;
    }

    return await query;
  }

  async getManufacturerDocumentsCount(filters?: ManufacturerDocumentFilters): Promise<number> {
    const conditions = this.buildFilterConditions(filters);

    let query = db.select({ count: sql<number>`count(*)` }).from(manufacturerDocuments);

    if (conditions) {
      query = query.where(conditions) as any;
    }

    const result = await query;
    return Number(result[0]?.count || 0);
  }

  private buildFilterConditions(filters?: ManufacturerDocumentFilters) {
    if (!filters) return undefined;

    const conditions = [];

    if (filters.search) {
      conditions.push(ilike(manufacturerDocuments.documentTitle, `%${filters.search}%`));
    }

    if (filters.manufacturers && filters.manufacturers.length > 0) {
      conditions.push(inArray(manufacturerDocuments.manufacturer, filters.manufacturers));
    }

    if (filters.systemTypes && filters.systemTypes.length > 0) {
      conditions.push(inArray(manufacturerDocuments.systemType, filters.systemTypes));
    }

    if (filters.documentTypes && filters.documentTypes.length > 0) {
      conditions.push(inArray(manufacturerDocuments.documentType, filters.documentTypes));
    }

    return conditions.length > 0 ? and(...conditions) : undefined;
  }

  async createManufacturerDocument(document: InsertManufacturerDocument): Promise<ManufacturerDocument> {
    const [newDocument] = await db.insert(manufacturerDocuments).values(document).returning();
    return newDocument;
  }
}

export const storage = new DbStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, integer } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const leads = pgTable("leads", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  firstName: text("first_name").notNull(),
  lastName: text("last_name").notNull(),
  email: text("email").notNull(),
  company: text("company"),
  phone: text("phone"),
  role: text("role"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const contacts = pgTable("contacts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  firstName: text("first_name").notNull(),
  lastName: text("last_name").notNull(),
  email: text("email").notNull(),
  company: text("company"),
  phone: text("phone"),
  inquiryType: text("inquiry_type"),
  message: text("message").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertLeadSchema = createInsertSchema(leads).omit({
  id: true,
  createdAt: true,
}).extend({
  email: z.string().email("Please enter a valid email address"),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
});

export const insertContactSchema = createInsertSchema(contacts).omit({
  id: true,
  createdAt: true,
}).extend({
  email: z.string().email("Please enter a valid email address"),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  message: z.string().min(10, "Message must be at least 10 characters"),
});

export type InsertLead = z.infer<typeof insertLeadSchema>;
export type Lead = typeof leads.$inferSelect;

export type InsertContact = z.infer<typeof insertContactSchema>;
export type Contact = typeof contacts.$inferSelect;

export const trainingCourses = pgTable("training_courses", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  duration: text("duration").notNull(),
  level: text("level").notNull(),
  category: text("category").notNull(),
  icon: text("icon").notNull(),
  sortOrder: integer("sort_order").notNull().default(0),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const trainingResources = pgTable("training_resources", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  icon: text("icon").notNull(),
  sortOrder: integer("sort_order").notNull().default(0),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertTrainingCourseSchema = createInsertSchema(trainingCourses).omit({
  id: true,
  createdAt: true,
});

export const insertTrainingResourceSchema = createInsertSchema(trainingResources).omit({
  id: true,
  createdAt: true,
});

export type InsertTrainingCourse = z.infer<typeof insertTrainingCourseSchema>;
export type TrainingCourse = typeof trainingCourses.$inferSelect;

export type InsertTrainingResource = z.infer<typeof insertTrainingResourceSchema>;
export type TrainingResource = typeof trainingResources.$inferSelect;

export const manufacturerDocuments = pgTable("manufacturer_documents", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  manufacturer: text("manufacturer").notNull(),
  documentTitle: text("document_title").notNull(),
  documentType: text("document_type").notNull(),
  systemType: text("system_type").notNull(),
  documentUrl: text("document_url").notNull(),
  thumbnailUrl: text("thumbnail_url"),
  dateAdded: timestamp("date_added").defaultNow().notNull(),
  lastUpdated: timestamp("last_updated").defaultNow().notNull(),
});

export const insertManufacturerDocumentSchema = createInsertSchema(manufacturerDocuments).omit({
  id: true,
  dateAdded: true,
  lastUpdated: true,
});

export type InsertManufacturerDocument = z.infer<typeof insertManufacturerDocumentSchema>;
export type ManufacturerDocument = typeof manufacturerDocuments.$inferSelect;

