import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import NotFound from "@/pages/not-found";
import Home from "@/pages/home";
import About from "@/pages/about";
import Features from "@/pages/features";
import Tools from "@/pages/tools";
import Pricing from "@/pages/pricing";
import Store from "@/pages/store";
import TrainingCenter from "@/pages/training-center";
import ManufacturerDocuments from "@/pages/manufacturer-documents";
import Contact from "@/pages/contact";
import Navigation from "@/components/navigation";

function Router() {
  return (
    <>
      <div className="cyberpunk-grid" />
      <div className="content-layer">
        <Navigation />
        <Switch>
          <Route path="/" component={Home} />
          <Route path="/about" component={About} />
          <Route path="/features" component={Features} />
          <Route path="/tools" component={Tools} />
          <Route path="/pricing" component={Pricing} />
          <Route path="/store" component={Store} />
          <Route path="/training-center" component={TrainingCenter} />
          <Route path="/manufacturer-documents" component={ManufacturerDocuments} />
          <Route path="/contact" component={Contact} />
          <Route component={NotFound} />
        </Switch>
      </div>
    </>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <TooltipProvider>
        <Toaster />
        <Router />
      </TooltipProvider>
    </QueryClientProvider>
  );
}

export default App;

