@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;500;600;700;800;900&family=Inter:wght@300;400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 0%);
  --foreground: hsl(0, 0%, 95%);
  --card: hsl(0, 0%, 5%);
  --card-foreground: hsl(0, 0%, 95%);
  --popover: hsl(0, 0%, 5%);
  --popover-foreground: hsl(0, 0%, 95%);
  --primary: hsl(197, 100%, 55%);
  --primary-foreground: hsl(0, 0%, 0%);
  --secondary: hsl(0, 0%, 10%);
  --secondary-foreground: hsl(0, 0%, 95%);
  --muted: hsl(0, 0%, 15%);
  --muted-foreground: hsl(197, 20%, 60%);
  --accent: hsl(197, 100%, 55%);
  --accent-foreground: hsl(0, 0%, 0%);
  --destructive: hsl(0, 100%, 50%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(197, 100%, 55%);
  --input: hsl(0, 0%, 10%);
  --ring: hsl(197, 100%, 55%);
  --radius: 4px;
  --font-sans: 'Inter', sans-serif;
  --font-display: 'Orbitron', sans-serif;
  --logo-blue: hsl(197, 100%, 55%);
  --logo-white: hsl(0, 0%, 95%);
  --glow-color: hsla(197, 100%, 55%, 0.6);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    position: relative;
    overflow-x: hidden;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-display);
    text-transform: uppercase;
    letter-spacing: 0.05em;
  }
}

@keyframes gridMove {
  0% {
    background-position: 0 0, 0 0;
  }
  100% {
    background-position: 50px 50px, 50px 50px;
  }
}

@keyframes glowPulse {
  0%, 100% {
    text-shadow: 
      0 0 10px var(--glow-color),
      0 0 20px var(--glow-color),
      0 0 30px var(--glow-color),
      0 0 40px var(--logo-blue);
  }
  50% {
    text-shadow: 
      0 0 5px var(--glow-color),
      0 0 10px var(--glow-color),
      0 0 15px var(--glow-color),
      0 0 20px var(--logo-blue);
  }
}

@keyframes borderGlow {
  0%, 100% {
    box-shadow: 
      0 0 5px var(--glow-color),
      0 0 10px var(--glow-color),
      inset 0 0 5px var(--glow-color);
  }
  50% {
    box-shadow: 
      0 0 10px var(--glow-color),
      0 0 20px var(--glow-color),
      inset 0 0 10px var(--glow-color);
  }
}

@layer utilities {
  .cyberpunk-grid {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: #000;
    background-image: 
      linear-gradient(rgba(27, 156, 252, 0.1) 1px, transparent 1px),
      linear-gradient(90deg, rgba(27, 156, 252, 0.1) 1px, transparent 1px);
    background-size: 50px 50px;
    animation: gridMove 20s linear infinite;
    pointer-events: none;
    z-index: 0;
  }

  .cyberpunk-grid::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 50% 50%, rgba(27, 156, 252, 0.05) 0%, transparent 70%);
  }

  .gradient-text {
    background: linear-gradient(135deg, hsl(197, 100%, 55%), hsl(197, 100%, 75%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: glowPulse 3s ease-in-out infinite;
  }

  .neon-glow {
    text-shadow: 
      0 0 10px var(--glow-color),
      0 0 20px var(--glow-color),
      0 0 30px var(--glow-color),
      0 0 40px var(--logo-blue);
    animation: glowPulse 3s ease-in-out infinite;
