client/src/pages/manufacturer-documents..content:
import { useState, useMemo } from "react";
import { FileText, Search, Filter, Download, ChevronLeft, ChevronRight } from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useQuery } from "@tanstack/react-query";
import type { ManufacturerDocument } from "@shared/schema";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";

const MANUFACTURERS = ["GAF", "Versico", "Carlisle", "Johns Manville", "Holcim Elevate"];
const SYSTEM_TYPES = ["TPO", "PVC", "EPDM", "Asphalt", "Coatings", "Insulation", "Accessories", "General"];
const DOCUMENT_TYPES = [
  "Product Data Sheet",
  "Technical Bulletin",
  "Installation Guide",
  "Specification",
  "Safety Data Sheet",
  "Warranty",
];

const ITEMS_PER_PAGE = 24;

export default function ManufacturerDocuments() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedManufacturers, setSelectedManufacturers] = useState<string[]>([]);
  const [selectedSystemTypes, setSelectedSystemTypes] = useState<string[]>([]);
  const [selectedDocumentTypes, setSelectedDocumentTypes] = useState<string[]>([]);
  const [showFilters, setShowFilters] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);

  const limit = ITEMS_PER_PAGE;
  const offset = (currentPage - 1) * ITEMS_PER_PAGE;

  const buildQueryString = () => {
    const params = new URLSearchParams();
    params.append('limit', limit.toString());
    params.append('offset', offset.toString());

    if (searchQuery) {
      params.append('search', searchQuery);
    }
    if (selectedManufacturers.length > 0) {
      params.append('manufacturers', selectedManufacturers.join(','));
    }
    if (selectedSystemTypes.length > 0) {
      params.append('systemTypes', selectedSystemTypes.join(','));
    }
    if (selectedDocumentTypes.length > 0) {
      params.append('documentTypes', selectedDocumentTypes.join(','));
    }

    return params.toString();
  };

  const { data: apiResponse, isLoading } = useQuery<{
    documents: ManufacturerDocument[];
    total: number;
    limit: number;
    offset: number;
  }>({
    queryKey: [`/api/manufacturer-documents?${buildQueryString()}`],
  });

  const documents = apiResponse?.documents || [];
  const totalDocuments = apiResponse?.total || 0;

  const totalPages = Math.ceil(totalDocuments / ITEMS_PER_PAGE);

  const paginatedDocuments = documents;

  const toggleFilter = (array: string[], setArray: (arr: string[]) => void, value: string) => {
    if (array.includes(value)) {
      setArray(array.filter((item) => item !== value));
    } else {
      setArray([...array, value]);
    }
    setCurrentPage(1);
  };

  const clearAllFilters = () => {
    setSelectedManufacturers([]);
    setSelectedSystemTypes([]);
    setSelectedDocumentTypes([]);
    setSearchQuery("");
    setCurrentPage(1);
  };

  const activeFiltersCount = selectedManufacturers.length + selectedSystemTypes.length + selectedDocumentTypes.length;

  return (
    <div className="min-h-screen pt-24 pb-20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-5xl md:text-6xl font-black mb-6 text-white">
            Manufacturer <span className="gradient-text neon-glow">Documents</span>
          </h1>
          <p className="text-xl text-gray-300 max-w-3xl mx-auto">
            Your Complete Resource Library
          </p>
        </div>

        {/* Search and Filter Toggle */}
        <div className="mb-8 flex flex-col md:flex-row gap-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <Input
              type="text"
              placeholder="Search documents..."
              value={searchQuery}
              onChange={(e) => {
                setSearchQuery(e.target.value);
                setCurrentPage(1);
              }}
              className="pl-10 bg-black/50 border-primary/30 text-white placeholder:text-gray-500"
              data-testid="input-search"
            />
          </div>
          <Button
            variant="outline"
            onClick={() => setShowFilters(!showFilters)}
            className="border-primary/30 text-primary hover:bg-primary/10"
            data-testid="button-toggle-filters"
          >
            <Filter className="mr-2 h-5 w-5" />
            Filters {activeFiltersCount > 0 && `(${activeFiltersCount})`}
          </Button>
          {activeFiltersCount > 0 && (
            <Button
              variant="ghost"
              onClick={clearAllFilters}
              className="text-gray-400 hover:text-white"
              data-testid="button-clear-filters"
            >
              Clear All
            </Button>
          )}
        </div>

        <div className="flex flex-col lg:flex-row gap-8">
          {/* Filter Sidebar */}
          {showFilters && (
            <aside className="lg:w-80 space-y-6">
              <Card className="glow-border bg-black/80 backdrop-blur-sm">
                <CardHeader>
                  <CardTitle className="text-white text-lg">Manufacturer</CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  {MANUFACTURERS.map((manufacturer) => (
                    <div key={manufacturer} className="flex items-center space-x-2">
                      <Checkbox
                        id={`manufacturer-${manufacturer}`}
                        checked={selectedManufacturers.includes(manufacturer)}
                        onCheckedChange={() => toggleFilter(selectedManufacturers, setSelectedManufacturers, manufacturer)}
                        className="border-primary data-[state=checked]:bg-primary data-[state=checked]:text-black"
                        data-testid={`checkbox-manufacturer-${manufacturer.toLowerCase().replace(/\s+/g, '-')}`}
                      />
                      <Label
                        htmlFor={`manufacturer-${manufacturer}`}
                        className="text-gray-300 cursor-pointer"
                      >
                        {manufacturer}
                      </Label>
                    </div>
                  ))}
                </CardContent>
              </Card>

              <Card className="glow-border bg-black/80 backdrop-blur-sm">
                <CardHeader>
                  <CardTitle className="text-white text-lg">System Type</CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  {SYSTEM_TYPES.map((systemType) => (
                    <div key={systemType} className="flex items-center space-x-2">
                      <Checkbox
                        id={`system-${systemType}`}
                        checked={selectedSystemTypes.includes(systemType)}
                        onCheckedChange={() => toggleFilter(selectedSystemTypes, setSelectedSystemTypes, systemType)}
                        className="border-primary data-[state=checked]:bg-primary data-[state=checked]:text-black"
                        data-testid={`checkbox-system-${systemType.toLowerCase()}`}
                      />
                      <Label
                        htmlFor={`system-${systemType}`}
                        className="text-gray-300 cursor-pointer"
                      >
                        {systemType}
                      </Label>
                    </div>
                  ))}
                </CardContent>
              </Card>

              <Card className="glow-border bg-black/80 backdrop-blur-sm">
                <CardHeader>
                  <CardTitle className="text-white text-lg">Document Type</CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  {DOCUMENT_TYPES.map((docType) => (
                    <div key={docType} className="flex items-center space-x-2">
                      <Checkbox
                        id={`doctype-${docType}`}
                        checked={selectedDocumentTypes.includes(docType)}
                        onCheckedChange={() => toggleFilter(selectedDocumentTypes, setSelectedDocumentTypes, docType)}
                        className="border-primary data-[state=checked]:bg-primary data-[state=checked]:text-black"
                        data-testid={`checkbox-doctype-${docType.toLowerCase().replace(/\s+/g, '-')}`}
                      />
                      <Label
                        htmlFor={`doctype-${docType}`}
                        className="text-gray-300 cursor-pointer"
                      >
                        {docType}
                      </Label>
                    </div>
                  ))}
                </CardContent>
              </Card>
            </aside>
          )}

          {/* Main Content Area */}
          <div className="flex-1">
            {isLoading ? (
              <div className="text-center text-gray-400 py-12">Loading documents...</div>
            ) : totalDocuments === 0 ? (
              <div className="text-center py-12">
                <FileText className="h-16 w-16 text-gray-600 mx-auto mb-4" />
                <p className="text-gray-400 text-lg">No documents found</p>
              </div>
            ) : (
              <>
                <div className="mb-6 flex justify-between items-center">
                  <div className="text-gray-400">
                    Showing {((currentPage - 1) * ITEMS_PER_PAGE) + 1}-{Math.min(currentPage * ITEMS_PER_PAGE, totalDocuments)} of {totalDocuments} document{totalDocuments !== 1 ? 's' : ''}
                  </div>
                  {totalPages > 1 && (
                    <div className="text-gray-400">
                      Page {currentPage} of {totalPages}
                    </div>
                  )}
                </div>
                <div className="grid md:grid-cols-2 xl:grid-cols-3 gap-6">
                  {paginatedDocuments.map((doc) => (
                    <Card
                      key={doc.id}
                      className="glow-border bg-black/80 backdrop-blur-sm hover:shadow-[0_0_30px_rgba(27,156,252,0.5)] transition-all"
                      data-testid={`card-document-${doc.id}`}
                    >
                      <CardHeader>
                        <div className="flex justify-center mb-4">
                          {doc.thumbnailUrl ? (
                            <img
                              src={doc.thumbnailUrl}
                              alt={doc.documentTitle}
                              className="h-32 w-32 object-contain"
                            />
                          ) : (
                            <div className="h-32 w-32 flex items-center justify-center bg-primary/10 border-2 border-primary rounded">
                              <FileText className="h-16 w-16 text-primary" />
                            </div>
                          )}
                        </div>
                        <div className="mb-3 flex justify-center">
                          <span className="px-3 py-1 bg-primary/20 text-primary rounded-full border border-primary text-sm font-bold" data-testid={`badge-manufacturer-${doc.id}`}>
                            {doc.manufacturer}
                          </span>
                        </div>
                        <CardTitle className="text-white text-center text-lg" data-testid={`text-title-${doc.id}`}>
                          {doc.documentTitle}
                        </CardTitle>
                      </CardHeader>
                      <CardContent>
                        <div className="flex flex-wrap gap-2 mb-4 justify-center">
                          <span className="px-3 py-1 bg-blue-500/20 text-blue-400 rounded-full border border-blue-500 text-xs" data-testid={`badge-system-${doc.id}`}>
                            {doc.systemType}
                          </span>
                          <span className="px-3 py-1 bg-purple-500/20 text-purple-400 rounded-full border border-purple-500 text-xs" data-testid={`badge-doctype-${doc.id}`}>
                            {doc.documentType}
                          </span>
                        </div>
                        <Button
                          className="w-full bg-primary hover:bg-primary/90 text-black font-bold glow-effect"
                          onClick={() => window.open(doc.documentUrl, '_blank')}
                          data-testid={`button-download-${doc.id}`}
                        >
                          <Download className="mr-2 h-5 w-5" />
                          Download
                        </Button>
                      </CardContent>
                    </Card>
                  ))}
                </div>

                {/* Pagination Controls */}
                {totalPages > 1 && (
                  <div className="mt-8 flex justify-center items-center gap-2">
                    <Button
                      variant="outline"
                      onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                      disabled={currentPage === 1}
                      className="border-primary/30 text-primary hover:bg-primary/10 disabled:opacity-50 disabled:cursor-not-allowed"
                      data-testid="button-prev-page"
                    >
                      <ChevronLeft className="h-5 w-5" />
                      Previous
                    </Button>

                    <div className="flex gap-2">
                      {[...Array(totalPages)].map((_, index) => {
                        const page = index + 1;
                        // Show first page, last page, current page, and pages around current
                        const showPage = page === 1 || page === totalPages || (page >= currentPage - 1 && page <= currentPage + 1);
                        const showEllipsis = (page === 2 && currentPage > 3) || (page === totalPages - 1 && currentPage < totalPages - 2);

                        if (showEllipsis) {
                          return <span key={page} className="text-gray-400 px-2">...</span>;
                        }

                        if (!showPage) return null;

                        return (
                          <Button
                            key={page}
                            variant={currentPage === page ? "default" : "outline"}
                            onClick={() => setCurrentPage(page)}
                            className={currentPage === page 
                              ? "bg-primary text-black hover:bg-primary/90" 
                              : "border-primary/30 text-primary hover:bg-primary/10"
                            }
                            data-testid={`button-page-${page}`}
                          >
                            {page}
                          </Button>
                        );
                      })}
                    </div>

                    <Button
                      variant="outline"
                      onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                      disabled={currentPage === totalPages}
                      className="border-primary/30 text-primary hover:bg-primary/10 disabled:opacity-50 disabled:cursor-not-allowed"
                      data-testid="button-next-page"
                    >
                      Next
                      <ChevronRight className="h-5 w-5" />
                    </Button>
                  </div>
                )}
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

