// ROI Calculator Component
function ROICalculator({ open, onClose }: { open: boolean; onClose: () => void }) {
  const [inputs, setInputs] = useState({
    projectCost: "",
    currentEnergyCost: "",
    energySavings: "",
    roofLifespan: "20",
    maintenanceSavings: ""
  });

  const calculate = () => {
    const cost = parseFloat(inputs.projectCost);
    const monthlyEnergy = parseFloat(inputs.currentEnergyCost);
    const savingsPercent = parseFloat(inputs.energySavings) / 100;
    const lifespan = parseFloat(inputs.roofLifespan);
    const maintenanceSavings = parseFloat(inputs.maintenanceSavings);

    const annualEnergySavings = monthlyEnergy * 12 * savingsPercent;
    const totalSavings = (annualEnergySavings * lifespan) + (maintenanceSavings * lifespan);
    const paybackPeriod = cost / (annualEnergySavings + maintenanceSavings);
    const annualROI = ((totalSavings - cost) / cost / lifespan) * 100;

    return {
      totalSavings: totalSavings.toFixed(2),
      paybackPeriod: paybackPeriod.toFixed(1),
      annualROI: annualROI.toFixed(2),
      breakEven: paybackPeriod.toFixed(1)
    };
  };

  const results = inputs.projectCost && inputs.currentEnergyCost && inputs.energySavings && inputs.maintenanceSavings 
    ? calculate() 
    : null;

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl bg-black/95 border-primary/30 max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl text-white flex items-center gap-2">
