            Roofing Project ROI Calculator
          </DialogTitle>
          <DialogDescription className="text-gray-400">
            Calculate return on investment for roofing projects
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 mt-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="roi-cost" className="text-gray-300">Project Cost ($)</Label>
              <Input
                id="roi-cost"
                type="number"
                value={inputs.projectCost}
                onChange={(e) => setInputs({ ...inputs, projectCost: e.target.value })}
                className="bg-black/50 border-primary/30 text-white"
                placeholder="50000"
                data-testid="input-roi-cost"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="roi-energy" className="text-gray-300">Current Energy Cost ($/month)</Label>
              <Input
                id="roi-energy"
                type="number"
                value={inputs.currentEnergyCost}
                onChange={(e) => setInputs({ ...inputs, currentEnergyCost: e.target.value })}
                className="bg-black/50 border-primary/30 text-white"
                placeholder="500"
                data-testid="input-roi-energy"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="roi-savings" className="text-gray-300">Expected Energy Savings (%)</Label>
              <Input
                id="roi-savings"
                type="number"
                value={inputs.energySavings}
                onChange={(e) => setInputs({ ...inputs, energySavings: e.target.value })}
                className="bg-black/50 border-primary/30 text-white"
                placeholder="25"
                data-testid="input-roi-savings"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="roi-lifespan" className="text-gray-300">Roof Lifespan (years)</Label>
              <Input
                id="roi-lifespan"
                type="number"
                value={inputs.roofLifespan}
                onChange={(e) => setInputs({ ...inputs, roofLifespan: e.target.value })}
                className="bg-black/50 border-primary/30 text-white"
                data-testid="input-roi-lifespan"
              />
            </div>
            <div className="space-y-2 col-span-2">
              <Label htmlFor="roi-maintenance" className="text-gray-300">Maintenance Cost Savings ($/year)</Label>
              <Input
                id="roi-maintenance"
                type="number"
