// Wind Uplift Calculator Component
function WindUpliftCalculator({ open, onClose }: { open: boolean; onClose: () => void }) {
  const [inputs, setInputs] = useState({
    buildingHeight: "",
    windSpeed: "110",
    roofZone: "Field",
    exposure: "C",
    roofSlope: "2"
  });

  const calculate = () => {
    const height = parseFloat(inputs.buildingHeight);
    const V = parseFloat(inputs.windSpeed);
    const slope = parseFloat(inputs.roofSlope);

    // Calculate Kz based on height and exposure (ASCE 7)
    let Kz = 0.85; // default for Exposure C at 30 ft

    if (inputs.exposure === "B") {
      // Suburban/wooded exposure
      Kz = height <= 30 ? 0.57 : 0.62 * Math.pow(height / 30, 2/7);
    } else if (inputs.exposure === "C") {
      // Open terrain exposure
      if (height <= 15) Kz = 0.85;
      else if (height <= 20) Kz = 0.90;
      else if (height <= 30) Kz = 0.98;
      else if (height <= 40) Kz = 1.04;
      else if (height <= 50) Kz = 1.09;
      else Kz = 1.09 * Math.pow(height / 50, 2/9.5);
    } else if (inputs.exposure === "D") {
      // Coastal/flat exposure
      if (height <= 15) Kz = 1.03;
      else if (height <= 20) Kz = 1.08;
      else if (height <= 30) Kz = 1.16;
      else if (height <= 40) Kz = 1.22;
      else if (height <= 50) Kz = 1.27;
      else Kz = 1.27 * Math.pow(height / 50, 2/11.5);
    }

    // Topographic factor (Kzt) - simplified to 1.0 for flat terrain
    const Kzt = 1.0;

    // Wind directionality factor (Kd) - 0.85 for buildings
    const Kd = 0.85;

    // Zone factors (GCp) - pressure coefficients
    let zoneFactors = { Corner: -2.8, Edge: -1.8, Field: -1.0 };

    // Slope adjustment for GCp (ASCE 7-16 Fig 27.3-1)
    // For slopes > 10¬∞, reduce negative pressure (less uplift)
    if (slope > 10) {
      const slopeReduction = 1.0 - (Math.min(slope - 10, 15) / 15) * 0.3; // Up to 30% reduction
      zoneFactors = {
        Corner: zoneFactors.Corner * slopeReduction,
        Edge: zoneFactors.Edge * slopeReduction,
        Field: zoneFactors.Field * slopeReduction
      };
    }

    const GCp = zoneFactors[inputs.roofZone as keyof typeof zoneFactors] || -1.0;

    // Calculate velocity pressure (qh)
    const qh = 0.00256 * Kz * Kzt * Kd * V * V;

    // Design pressure (using absolute value for uplift)
    const designPressure = Math.abs(qh * GCp);

    // Fastener spacing based on pressure
    const fastenerSpacing = designPressure > 40 ? "6 inches" : 
                           designPressure > 25 ? "12 inches" : 
                           designPressure > 15 ? "18 inches" : "24 inches";

    // Attachment pattern
    const pattern = inputs.roofZone === "Corner" ? "Enhanced corner fastening (2 rows)" : 
                   inputs.roofZone === "Edge" ? "Perimeter reinforcement (1.5x density)" : 
                   "Standard field pattern";

    return {
      designPressure: designPressure.toFixed(2),
      fastenerSpacing,
      pattern,
      safetyFactor: "1.5 (ASCE 7)"
    };
  };

  const results = inputs.buildingHeight && inputs.windSpeed ? calculate() : null;

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl bg-black/95 border-primary/30 max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl text-white flex items-center gap-2">
            <Wind className="h-6 w-6 text-primary" />
            Wind Uplift Pressure Calculator
          </DialogTitle>
          <DialogDescription className="text-gray-400">
            Calculate wind uplift forces for proper fastener selection (ASCE 7)
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 mt-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="wind-height" className="text-gray-300">Building Height (feet)</Label>
              <Input
                id="wind-height"
                type="number"
                value={inputs.buildingHeight}
                onChange={(e) => setInputs({ ...inputs, buildingHeight: e.target.value })}
                className="bg-black/50 border-primary/30 text-white"
                placeholder="30"
                data-testid="input-wind-height"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="wind-speed" className="text-gray-300">Wind Speed (mph)</Label>
              <Select 
                value={inputs.windSpeed} 
                onValueChange={(value) => setInputs({ ...inputs, windSpeed: value })}
              >
                <SelectTrigger className="bg-black/50 border-primary/30 text-white" data-testid="select-wind-speed">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="90">90 mph</SelectItem>
                  <SelectItem value="110">110 mph</SelectItem>
                  <SelectItem value="130">130 mph</SelectItem>
                  <SelectItem value="150">150 mph</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="wind-zone" className="text-gray-300">Roof Zone</Label>
              <Select 
                value={inputs.roofZone} 
                onValueChange={(value) => setInputs({ ...inputs, roofZone: value })}
              >
                <SelectTrigger className="bg-black/50 border-primary/30 text-white" data-testid="select-wind-zone">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="Corner">Corner</SelectItem>
                  <SelectItem value="Edge">Edge</SelectItem>
                  <SelectItem value="Field">Field</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="wind-exposure" className="text-gray-300">Building Exposure</Label>
              <Select 
                value={inputs.exposure} 
                onValueChange={(value) => setInputs({ ...inputs, exposure: value })}
              >
                <SelectTrigger className="bg-black/50 border-primary/30 text-white" data-testid="select-wind-exposure">
                  <SelectValue />
                </SelectTrigger>
