// Square Footage Calculator Component
function SquareFootageCalculator({ open, onClose }: { open: boolean; onClose: () => void }) {
  const [inputs, setInputs] = useState({
    roofType: "Flat",
    roofLength: "",
    roofWidth: "",
    pitch: "4",
    wasteFactor: "10"
  });

  const calculate = () => {
    const length = parseFloat(inputs.roofLength);
    const width = parseFloat(inputs.roofWidth);
    const waste = 1 + parseFloat(inputs.wasteFactor) / 100;

    let baseArea = length * width;

    // Adjust for roof type
    if (inputs.roofType === "Gable" || inputs.roofType === "Hip") {
      const pitch = parseFloat(inputs.pitch);
      const pitchMultiplier = Math.sqrt(1 + Math.pow(pitch / 12, 2));
      baseArea = baseArea * pitchMultiplier;
    }

    const totalArea = baseArea * waste;
    const squares = baseArea / 100;
    const rollSize = 100; // Standard roll coverage
    const rolls = Math.ceil(totalArea / rollSize);

    return {
      baseArea: baseArea.toFixed(2),
      totalArea: totalArea.toFixed(2),
      squares: squares.toFixed(2),
      rolls
    };
  };

  const results = inputs.roofLength && inputs.roofWidth ? calculate() : null;

  const clearForm = () => {
    setInputs({
      roofType: "Flat",
      roofLength: "",
      roofWidth: "",
      pitch: "4",
      wasteFactor: "10"
