// Insulation Calculator Component
function InsulationCalculator({ open, onClose }: { open: boolean; onClose: () => void }) {
  const [inputs, setInputs] = useState({
    climateZone: "4",
    buildingType: "Commercial",
    currentRValue: "",
    targetSavings: "20"
  });

  const calculate = () => {
    const zone = parseInt(inputs.climateZone);
    const currentR = parseFloat(inputs.currentRValue) || 0;
    const savingsPercent = parseFloat(inputs.targetSavings);

    // Recommended R-values by climate zone
    const recommendedR = zone <= 2 ? 20 : zone <= 4 ? 30 : zone <= 6 ? 40 : 50;

    // Calculate needed R-value increase
    const neededR = Math.max(recommendedR - currentR, 0);

    // Thickness estimates (inches per R-value for common materials)
    const polyisoThickness = (neededR / 6).toFixed(1); // R-6 per inch
    const xpsThickness = (neededR / 5).toFixed(1); // R-5 per inch
    const epsThickness = (neededR / 4).toFixed(1); // R-4 per inch

    // Energy savings estimate (simplified)
    const annualSavings = (savingsPercent / 100) * 2000; // Base $2000/year

    return {
      recommendedR: recommendedR.toString(),
      neededR: neededR.toFixed(1),
      annualSavings: annualSavings.toFixed(0),
      polyisoThickness,
      xpsThickness,
      epsThickness
    };
  };

  const results = inputs.climateZone ? calculate() : null;

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl bg-black/95 border-primary/30 max-h-[90vh] overflow-y-auto">
