// Project Estimator Component
function ProjectEstimator({ open, onClose }: { open: boolean; onClose: () => void }) {
  const [inputs, setInputs] = useState({
    roofArea: "",
    systemType: "TPO",
    installMethod: "Fully Adhered",
    insulation: "Yes",
    tearoff: "Yes",
    region: "Midwest"
  });

  const calculate = () => {
    const area = parseFloat(inputs.roofArea);

    // Base costs per sq ft
    const materialCosts: any = {
      TPO: 3.5,
      PVC: 4.0,
      EPDM: 3.0,
      "Modified Bitumen": 3.25,
      "Built-Up": 3.75
    };

    const laborCosts: any = {
      "Fully Adhered": 2.5,
      "Mechanically Attached": 2.0,
      "Ballasted": 1.75
    };

    const regionMultipliers: any = {
      Northeast: 1.2,
      Southeast: 1.0,
      Midwest: 1.05,
      West: 1.15,
      Southwest: 1.0
    };

    let materialCost = area * (materialCosts[inputs.systemType] || 3.5);
    let laborCost = area * (laborCosts[inputs.installMethod] || 2.0);

    if (inputs.insulation === "Yes") {
      materialCost += area * 2.0;
      laborCost += area * 0.5;
    }

    if (inputs.tearoff === "Yes") {
      laborCost += area * 1.5;
    }

    const regionMultiplier = regionMultipliers[inputs.region] || 1.0;
    materialCost *= regionMultiplier;
    laborCost *= regionMultiplier;

    const totalLow = (materialCost + laborCost) * 0.9;
    const totalMid = materialCost + laborCost;
    const totalHigh = (materialCost + laborCost) * 1.2;

    const costPerSquare = (totalMid / area) * 100;
    const estimatedDays = Math.ceil(area / 2000);

    return {
      materialCost: materialCost.toFixed(0),
      laborCost: laborCost.toFixed(0),
      totalLow: totalLow.toFixed(0),
      totalMid: totalMid.toFixed(0),
      totalHigh: totalHigh.toFixed(0),
      costPerSquare: costPerSquare.toFixed(2),
      estimatedDays
    };
  };

  const results = inputs.roofArea ? calculate() : null;

  return (
