// Slope Calculator Component
function SlopeCalculator({ open, onClose }: { open: boolean; onClose: () => void }) {
  const [inputs, setInputs] = useState({
    inputFormat: "Rise/Run",
    rise: "4",
    run: "12",
    value: ""
  });

  const calculate = () => {
    let rise = 0, run = 12;

    if (inputs.inputFormat === "Rise/Run") {
      rise = parseFloat(inputs.rise);
      run = parseFloat(inputs.run);
    } else if (inputs.inputFormat === "Degrees") {
      const degrees = parseFloat(inputs.value);
      rise = Math.tan(degrees * Math.PI / 180) * 12;
      run = 12;
    } else if (inputs.inputFormat === "Percentage") {
      const percent = parseFloat(inputs.value) / 100;
      rise = percent * 12;
      run = 12;
    } else if (inputs.inputFormat === "Inches per Foot") {
      rise = parseFloat(inputs.value);
      run = 12;
    }

    const slopeDegrees = Math.atan(rise / run) * (180 / Math.PI);
    const slopePercent = (rise / run) * 100;
    const pitch = `${rise.toFixed(1)}:${run}`;

    // Drainage recommendations
    const minSlope = rise / run >= 0.25 / 12 ? "Adequate" : "Insufficient";
    const recommendation = rise / run < 0.25 / 12 ? "Minimum 1/4\" per foot required" :
                          rise / run < 2 / 12 ? "Suitable for most single-ply membranes" :
                          rise / run < 4 / 12 ? "Ideal for drainage" :
                          "Consider steep slope specifications";

    return {
      degrees: slopeDegrees.toFixed(2),
      percent: slopePercent.toFixed(2),
      pitch,
      riseRun: `${rise.toFixed(2)}:${run}`,
      inchesPerFoot: (rise / run * 12).toFixed(2),
      minSlope,
      recommendation
    };
  };

  const results = (inputs.inputFormat === "Rise/Run" && inputs.rise && inputs.run) || 
                  (inputs.inputFormat !== "Rise/Run" && inputs.value) 
    ? calculate() 
    : null;

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl bg-black/95 border-primary/30 max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl text-white flex items-center gap-2">
            <Calculator className="h-6 w-6 text-primary" />
            Roof Slope/Pitch Calculator
          </DialogTitle>
          <DialogDescription className="text-gray-400">
            Convert between slope formats and calculate drainage
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 mt-4">
          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="slope-format" className="text-gray-300">Input Format</Label>
              <Select 
                value={inputs.inputFormat} 
                onValueChange={(value) => setInputs({ ...inputs, inputFormat: value })}
              >
                <SelectTrigger className="bg-black/50 border-primary/30 text-white" data-testid="select-slope-format">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="Rise/Run">Rise/Run</SelectItem>
                  <SelectItem value="Degrees">Degrees</SelectItem>
                  <SelectItem value="Percentage">Percentage</SelectItem>
                  <SelectItem value="Inches per Foot">Inches per Foot</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {inputs.inputFormat === "Rise/Run" ? (
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="slope-rise" className="text-gray-300">Rise</Label>
                  <Input
                    id="slope-rise"
                    type="number"
                    value={inputs.rise}
                    onChange={(e) => setInputs({ ...inputs, rise: e.target.value })}
                    className="bg-black/50 border-primary/30 text-white"
                    data-testid="input-slope-rise"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="slope-run" className="text-gray-300">Run</Label>
                  <Input
                    id="slope-run"
                    type="number"
                    value={inputs.run}
                    onChange={(e) => setInputs({ ...inputs, run: e.target.value })}
                    className="bg-black/50 border-primary/30 text-white"
                    data-testid="input-slope-run"
                  />
                </div>
              </div>
            ) : (
              <div className="space-y-2">
                <Label htmlFor="slope-value" className="text-gray-300">Value</Label>
                <Input
                  id="slope-value"
                  type="number"
                  value={inputs.value}
                  onChange={(e) => setInputs({ ...inputs, value: e.target.value })}
                  className="bg-black/50 border-primary/30 text-white"
                  placeholder={
                    inputs.inputFormat === "Degrees" ? "30" :
                    inputs.inputFormat === "Percentage" ? "50" :
                    "4"
                  }
                  data-testid="input-slope-value"
                />
              </div>
            )}
          </div>

          {results && (
            <div className="space-y-4">
              <div className="p-6 bg-primary/10 border border-primary rounded-lg space-y-3" data-testid="result-slope">
                <h3 className="text-primary font-bold text-lg mb-4">All Format Conversions</h3>
                <div className="grid grid-cols-2 gap-3">
                  <div className="flex justify-between">
                    <span className="text-gray-400">Degrees:</span>
                    <span className="text-white font-bold">{results.degrees}¬∞</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-400">Percentage:</span>
                    <span className="text-white font-bold">{results.percent}%</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-400">Pitch:</span>
                    <span className="text-white font-bold">{results.pitch}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-400">Rise/Run:</span>
                    <span className="text-white font-bold">{results.riseRun}</span>
                  </div>
                  <div className="flex justify-between col-span-2">
                    <span className="text-gray-400">Inches per Foot:</span>
                    <span className="text-white font-bold">{results.inchesPerFoot}&quot;</span>
                  </div>
                </div>
              </div>

              <div className="p-6 bg-black/50 border border-primary/20 rounded-lg">
                <h4 className="text-white font-semibold mb-3">Drainage Analysis</h4>
                <div className="space-y-2">
                  <div className="flex justify-between items-center">
                    <span className="text-gray-400">Minimum Slope:</span>
                    <span className={`font-bold ${results.minSlope === "Adequate" ? "text-green-400" : "text-red-400"}`}>
                      {results.minSlope}
                    </span>
                  </div>
                  <div className="pt-2 border-t border-primary/20">
                    <p className="text-gray-400 text-sm mb-1">Recommendation:</p>
                    <p className="text-primary">{results.recommendation}</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          <div className="flex gap-3">
            <Button 
              onClick={onClose}
              variant="outline"
              className="flex-1 border-primary/30 text-primary hover:bg-primary/10"
              data-testid="button-close-slope"
            >
              Close
            </Button>
            <Button 
              onClick={() => setInputs({ inputFormat: "Rise/Run", rise: "4", run: "12", value: "" })}
              className="flex-1 bg-primary/20 text-primary hover:bg-primary/30"
              data-testid="button-clear-slope"
            >
              Clear
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

